class Home extends StatefulWidget {
  @override
  _HomeState createState() => _HomeState();
}

class _HomeState extends State<Home> {
  List<MapEntry> devicesList = []; // Initialize as empty list
  final storage = FlutterSecureStorage(); // Assuming you have this

  @override
  void initState() {
    super.initState();
    _fetchDevices();
  }

  Future<void> _fetchDevices() async {
    String? token = await storage.read(key: 'jwt');
    final response = await http.get(
      Uri.parse('http://5.196.23.212:8080/get_devices'),
      headers: {
        'Authorization': 'Bearer $token',
      },
    );

    if (response.statusCode == 200) {
      setState(() {
        devicesList = jsonDecode(response.body).entries.toList();
        print("cock"); // Consider changing this to a more meaningful message
      });
    } else {
      // TODO: Handle the error appropriately
    }
  }

  Future<void> _refreshList() async {
    await _fetchDevices();
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: const Color(0xffecf3e8),
      appBar: AppBar(
        backgroundColor: Colors.transparent,
        elevation: 0.0, // Remove shadow
        title: Text(
          'Beam Digital SRL',
          style: TextStyle(
            fontFamily: 'Lato',
            fontSize: 20,
            color: const Color(0xff27312f),
            fontWeight: FontWeight.w700,
          ),
        ),
        actions: [
          IconButton(
            icon: Icon(Icons.refresh,
                color: const Color(0xff27312f),
                size: 28.0), // Refresh Icon with size 28.0
            onPressed: () {
              // Add the action for the refresh button here.
              _refreshList();
            },
          ),
          IconButton(
            icon: Icon(Icons.account_circle,
                color: const Color(0xff27312f),
                size: 28.0), // Account Icon with size 28.0
            onPressed: () {
              // Add the action for the account button here.
            },
          ),
          IconButton(
            icon: SvgPicture.string(_svg_yfxl45),
            onPressed: () {
              // Add the action for the settings SVG icon here.
            },
          ),
        ],
      ),
      body: Padding(
        padding: const EdgeInsets.symmetric(horizontal: 16.0),
        child: Column(
          children: [
            SizedBox(height: 20.0),
            Image.asset('assets/Log1.png', height: 65.0, fit: BoxFit.fill),
            SizedBox(height: 20.0),
            GestureDetector(
              onTap: () {
                // Add the action for the list header here.
              },
              child: Container(
                padding: EdgeInsets.symmetric(vertical: 16.0, horizontal: 10.0),
                child: Row(
                  mainAxisAlignment: MainAxisAlignment.spaceBetween,
                  children: [
                    Text(
                      'Connected devices',
                      style: TextStyle(
                        fontFamily: 'Lato',
                        fontSize: 20,
                        color: const Color(0xff27312f),
                        fontWeight: FontWeight.w700,
                      ),
                    ),
                    Icon(Icons.arrow_forward_ios,
                        color: const Color(0xff27312f)),
                  ],
                ),
              ),
            ),
            SizedBox(height: 20.0),
            Expanded(
              child: RefreshIndicator(
                onRefresh: _refreshList,
                child: devicesList.isEmpty
                    ? Center(child: CircularProgressIndicator())
                    : ListView.builder(
                        physics: AlwaysScrollableScrollPhysics(),
                        itemCount: devicesList.length,
                        itemBuilder: (context, index) {
                          return _listItem(
                              devicesList[index].key, devicesList[index].value);
                        },
                      ),
              ),
            ),
          ],
        ),
      ),
    );
  }

  Widget _listItem(String macAddress, String? alias) {
    return Padding(
      padding: const EdgeInsets.symmetric(vertical: 8.0, horizontal: 25.0),
      child: InkWell(
        onTap: () {
          // Place your callback logic here
          print('Item with MAC $macAddress clicked');
        },
        child: Row(
          mainAxisAlignment: MainAxisAlignment.spaceBetween,
          children: [
            Icon(Icons.medical_services_outlined,
                size: 30.0, color: const Color(0xff27312f)),
            SizedBox(
                width:
                    16.0), // This will add space between the icon and the texts
            Expanded(
              child: Column(
                crossAxisAlignment: CrossAxisAlignment.start,
                children: [
                  Text(
                    'First AID Kit',
                    style: TextStyle(
                      fontFamily: 'Lato',
                      fontSize: 16,
                      color: const Color(0xff27312f),
                      fontWeight: FontWeight.w700,
                    ),
                  ),
                  Text(
                    // input alias as subtitle if alias isn't null, put mac address as subtitle otherwise
                    alias ?? macAddress,
                    style: TextStyle(
                      fontFamily: 'Lato',
                      fontSize: 12,
                      color: const Color(0xff27312f),
                      fontWeight: FontWeight.w700,
                    ),
                  ),
                ],
              ),
            ),
            Icon(Icons.arrow_forward_ios, color: const Color(0xff27312f)),
          ],
        ),
      ),
